import {React,useState} from "react";
import "../styles/sendreceive.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
const Search = () => {
  function post(p) {
        axios.post("http://localhost:4444/home/sendrequest", {
          aadharid: aadharuid,

        }, {
          headers: {
            "Authorization": "Bearer " + localStorage.getItem("jwt"),
            "Accept": "application/json",
            "Content-Type": "application/json",
            "Access-Control-Allow-Credentials": true,
          }
      })
      .then((res) => {
          if (res.data.s === "y") {
            console.log("succes");
            toast.success("Logged in succesfully!");
            localStorage.setItem("jwt", res.data.t);
            history.push("/home");
          } else {
            toast.error("error!");
            document.getElementById("otpb").disabled = false;
            let x = document.getElementsByClassName("otpcontainer");
            x[0].style.display = "none";
          }
      })
      .catch((error) => {
          console.log(error)
      })
  }
  let [passcodeinp,setPassInp] = useState();
  return (
    <div className="searchCodeContainer">
      <input
        className="searchCode"
        type="text"
        id="aadharNo"
        name="aadharNo"
        maxLength="12"
        value={passcodeinp}
        onChange={(e) => {
          setPassInp(e.target.value);
        }}
        required
        placeholder="Enter Passcode to send request"
      />
      <button type="submit" className="submit" onClick={()=>{post}}>
        <FontAwesomeIcon icon={faChevronRight} size="3x" />
      </button>
    </div>
  );
};

export default Search;
