    //     headers: {
    //       "Authorization": "Bearer " + localStorage.getItem("jwt"),
    //       "Accept": "application/json",
    //       "Content-Type": "application/json",
    //       "Access-Control-Allow-Credentials": true,
    //     }
    // })
    // .then((res) => {
    //     if (res.data.s === "y") {
    //       console.log("succes");
    //       toast.success("Logged in succesfully!");
    //       localStorage.setItem("jwt", res.data.t);
    //       history.push("/home");
    //     } else {
    //       toast.error("error!");
    //       document.getElementById("otpb").disabled = false;
    //       let x = document.getElementsByClassName("otpcontainer");
    //       x[0].style.display = "none";
    //     }
    // })
    // .catch((error) => {
    //     console.log(error)
    // })
  }
  function passcodet(params) {
    setPasscode(true);
    setSearch(false);
    document.getElementById("r").style.backgroundColor = "red";
    document.getElementById("s").style.backgroundColor = "cornflowerblue";
    setloading(true)
      axios
      .get(`http://localhost:4444/home/`, {
        withCredentials: true,
        headers: {
          Authorization: "Bearer " + localStorage.getItem("jwt"),
          Accept: "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Credentials": true,
        },
      })
      .then((res) => {
        setloading(false);
      });
  }
  return (
    <div className="containerSR">
       <div className="searchCodeContainer">
        {(search && !loading) && <Search />}
        {(passcode && !loading) && <Passcode />}
        {loading && (<Loader/>)}
      </div>

      <div className="sendReceiveContainer">
        <button className="srButton" id="s" onClick={() => searcht()}>
          <FontAwesomeIcon icon={faPaperPlane} size="2x" />
        </button>
        <button className="srButton" id="r" onClick={() => passcodet()}>
          <FontAwesomeIcon icon={faArrowAltCircleDown} size="3x" />
        </button>
      </div>
    </div>
  );
};
