import React, { useState, useEffect } from "react";
// import Select from "react-select";
import { useHistory } from "react-router";
// import Multiselect from 'multiselect-react-dropdown';
import "../styles/Addform.css";
import axios from "axios";

const Teamup_form = () => {
  const [Houseno, setHouseno] = useState("");
  const [street, setstreet] = useState("");
  const [Tag, settag] = useState([]);
  const [btn_disable, setbtn_disable] = useState(false);
  const [Description, setDescription] = useState("");
  let history = useHistory();

  const HandleInputs = async (event) => {
    setbtn_disable(true);
    event.preventDefault();
    let url = `http://localhost:4444/teamup/submit`;
    if (Tag.length === 0) {
      alert("Tag field should not be empty");
      setbtn_disable(false);
      console.log(btn_disable);
    } else {
      var tag_list = [];

      for (let i = 0; i < Tag.length; i++) {
        tag_list.push(Tag[i].value);
      }

      //   Tag.map((tag) => {
      //     tag_list.push(tag.value);
      //   });
      const datatobesent = {
        Houseno: Houseno,
        street: street,
        tag: tag_list,
        description: Description,
      };
      console.log(datatobesent);
      const res = await axios.post(url, datatobesent, {
        withCredentials: true,
      });
      console.log(res.status);
      if (res.status === 200) {
        // return <Redirect to="/myposts"/>
        history.push("/myposts");
      } else {
        setbtn_disable(false);
      }
    }
  };
  useEffect(() => {
    if (Houseno.length > 50) {
      alert("Houseno cannot be more than 50 characters");
      setHouseno(Houseno.slice(0, 50));
    }
    if (street.length > 50) {
      alert("street cannot be more than 50 characters");
      setstreet(street.slice(0, 50));
    }
    if (Description.length > 300) {
      alert("Description cannot be more than 300 characters");
      setDescription(Description.slice(0, 300));
    }
  }, [Houseno, street, Description]);

  return (
    <div>
      <form className="Form" onSubmit={HandleInputs}>
        <div className="Houseno">
          <p>Aadhar Address Update Form</p>
        </div>
        <div className="forminput">
          <label>
            House No/Bldg/Apt<span style={{ color: "red" }}>*</span>
          </label>
          <input
            className="input"
            type="text"
            required
            id="name"
            placeholder="Looking for WEB developer"
            value={Houseno}
            onChange={(e) => setHouseno(e.target.value)}
          ></input>
        </div>
        <div className="forminput">
          <label>
            Street/Road/Lane<span style={{ color: "red" }}>*</span>
          </label>
          <input
            className="input"
            type="text"
            required
            id="street"
            placeholder="Reactjs,kotlin"
            value={street}
            onChange={(e) => setstreet(e.target.value)}
          ></input>
        </div>

        <div className="forminput">
          <label>
            Street/Road/Lane<span style={{ color: "red" }}>*</span>
          </label>
          <input
            className="input"
            type="text"
            required
            id="street"
            placeholder="Reactjs,kotlin"
            value={street}
            onChange={(e) => setstreet(e.target.value)}
          ></input>
        </div>

        {/* <div className="forminput">
          <label>Description</label>
          <textarea
            rows="5"
            cols="50"
            placeholder="Enter teamup Description"
            value={Description}
            onChange={(e) => setDescription(e.target.value)}
          ></textarea>
        </div> */}
        <div className="btndiv">
          <button className="btn" type="submit" disabled={btn_disable}>
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default Teamup_form;
