import { React, useState } from "react";
import "../styles/Scard.css";
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
// import { faEdit } from "@fortawesome/free-solid-svg-icons";
import Ekycform from "./Ekycform";
import { toast } from "react-toastify";
import axios from "axios";
const Rcard = (props) => {
  const [form, setform] = useState(false);
  const [status, setstatus] = useState(props.status);
  function reject() {
    document.getElementById("rej").disable=true;
    axios.post("http://localhost:4444/receivedrequest/reject", {
          postid:props.ID
      }, {
        headers: {
          "Authorization": "Bearer " + localStorage.getItem("jwt"),
          "Accept": "application/json",
          "Content-Type": "application/json",
          "Access-Control-Allow-Credentials": true,
        }
    })
    .then((res) => {
        console.log(res);
        if (res.status === 200) {
            toast.success("Rejected")
        } else {
            toast.success("not Rejected")
        }
    })
    .catch((error) => {
        console.log(error)
    })
    console.log(props.key)
  }
  return (
    <div className="sbox">
      <div className="scard">
        <div className="cbox" style={{ height: "100px" }}>
          <p> {props.BearersID} is requesting the address</p>
        </div>
        <div className="cbox" style={{ marginTop: "auto", height: "50px" }}>
           {(status===0) && (<button className="btn reject" id="rej" onClick={()=> reject()} >
            <span>Reject</span>
          </button>)}
          {(status===-1) && (<button className="btn reject" style={{background:"red"}}>
            <span>Rejected</span>
          </button>)}
          {((status===0) && <button
            className="btn edit"
            onClick={() => {
              setform(!form);
            }}
            style={{ fontSize: "small" }}
          >
            <span>Accept</span>
          </button>)}
          {((status===1) && <button
            className="btn edit"
            onClick={() => {
              setform(!form);
            }}
            style={{ fontSize: "small" }}
          >
            <span>See More</span>
          </button>)}
        </div>
        {form && <Ekycform status={props.status} landLordAddress={props.landLordAddress} changedaddress={props.changedAddress}/>}
      </div>
    </div>
  );
};

export default Rcard;
