import { React, useState } from "react";
import "../styles/Scard.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit } from "@fortawesome/free-solid-svg-icons";
import Ekycform from "./Ekycform";
import { toast } from "react-toastify";
const Rcard = (props) => {
  const [form, setform] = useState(false);
  const [status, setstatus] = useState(props.status);
  return (
    <div className="sbox">
      <div className="scard">
        <div className="cbox" style={{ height: "100px" }}>
          <p> x is requesting the address</p>
        </div>
        <div className="cbox" style={{ marginTop: "auto", height: "50px" }}>
           {(status!==-1) && (<button className="btn reject" >
            <span>Reject</span>
          </button>)}
          {(status==-1) && (<button className="btn reject" style={{background:"red"}}>
            <span>Rejected</span>
          </button>)}
          {((status!==-1) && <button
            className="btn edit"
            onClick={() => {
              setform(!form);
            }}
            style={{ fontSize: "small" }}
          >
            <span>Accept</span>
          </button>)}
        </div>
        {form && <Ekycform />}
      </div>
    </div>
  );
};

export default Rcard;
